import axios from "axios";

export const getTodos = async (url) => {
  try {
    const response = await axios.get(url);
    return response
  } catch (err) {
    console.error(err.message || err)
  }
}

export async function postTodo(url, task) {
  try {
        const response = await axios
            .post({url: url }, {data: task}, {
                headers: {
                  'Content-Type': 'application/json'
                }
              });
        return response.data;
    } catch (error) {
        return error.message;
    }
}

export async function deleteTodo(url, id) {
  try {
        const response = await axios
            .delete(`/${url}/${id}`);
        return response.data;
    } catch (error) {
        return error.message;
    }
}

export async function updateTodo(url, task) {
  try {
        const response = await axios
            .patch(`/${url}/${task.id}`, { isDone: !task.isDone });
        return response.data;
    } catch (error) {
        return error.message;
    }
}
