import { useState } from 'react';
TodoService = require('../Services')

export default url => {
  const [todos, setTodos] = useState(TodoService.getTodos(url));

  return {
    todos,

    addTodo: async todo => {
      const newTodos = await TodoService.postTodo(url, todo);
      setTodos(newTodos);
    },

    updateTodo: async todo => {
      const newTodo= await TodoService.updateTodo(url, todo);
      setTodos(newTodo);
    },
    
    deleteTodo: async id => {
      await TodoService.deleteTodo(url, id);
      const newTodos = await TodoService.getTodos(url);
      setTodos(newTodos);
    }
  };
};
