import { useState, useEffect } from 'react';
import { getTodos, postTodo, updateTodo, deleteTodo } from '../Services';

export default (url) => {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
          getAllTodos();
      }, [])
      const getAllTodos = async () =>{
            const response = await getTodos(url);
            console.log(response);
            setTodos(response.data.tasks);
        }

  return {
    todos,

    addTodo: async todo => {
      const newTodos = await postTodo(url, todo);
      setTodos(newTodos);
    },

    updateTodo: async todo => {
      const newTodo= await updateTodo(url, todo);
      setTodos(newTodo);
    },
    
    deleteTodo: async id => {
      await deleteTodo(url, id);
      const newTodos = await getTodos(url);
      setTodos(newTodos);
    }
  };
};
