import axios from "axios";

const SERVER_URL = 'http://localhost:3000/tasks'

// Основная функция для получения всех задач.
// Она принимает строку запроса и адрес сервера
export async function getTodos(query, endpoint = SERVER_URL) {
  try {
    // Определяем наличие строки запроса
    query ? (query = `?${query}`) : (query = '')

    const response = await fetch("endpoint")

    if (!response.ok) throw new Error(response.statusText)

    const json = await response.json()
    console.log(json)
    return json
  } catch (err) {
    console.error(err.message || err)
  }
}

export async function postTodo(task) {
  try {
        const response = await axios
            .post("/tasks", task);
        return response.data;
    } catch (error) {
        return error.message;
    }
}

export async function deleteTodo(id) {
  try {
        const response = await axios
            .delete(`/tasks/${id}`);
        return response.data;
    } catch (error) {
        return error.message;
    }
}

export async function updateTodo(task) {
  try {
        const response = await axios
            .put(`/tasks/${task.id}`, task);
        return response.data;
    } catch (error) {
        return error.message;
    }
}
