import { useState, useEffect } from 'react';
import { getTodos, postTodo, updateTodo, deleteTodo } from '../Services';

export default () => {
  const [todos, setTodos] = useState([]);

  useEffect(() => {
          getAllTodos();
      }, [])
      const getAllTodos = async () =>{
            const response = await getTodos("db.json");
            console.log(response);
            setTodos(response.data);
        }

  return {
    todos,

    addTodo: async todo => {
      const newTodos = await postTodo("db.json", todo);
      setTodos(newTodos);
    },

    updateTodo: async todo => {
      const newTodo= await updateTodo("db.json", todo);
      setTodos(newTodo);
    },
    
    deleteTodo: async id => {
      await deleteTodo("db.json", id);
      const newTodos = await getTodos("db.json");
      setTodos(newTodos);
    }
  };
};
