import {React, useEffect, useState} from 'react';
import { List } from '@mui/material';
import {ListItem} from '@mui/material';
import {ListItemSecondaryAction} from '@mui/material';
import {ListItemText} from '@mui/material';
import {Checkbox} from '@mui/material';
import {IconButton} from '@mui/material';
import { DeleteForever } from '@mui/icons-material';
import { getTodos } from '../Services';

// const url = "//localhost:3000/tasks";

const TodoList = ({ todos, updateTodo, deleteTodo}) => {
//   const [todo, setTodo] = useState([]);
//   useEffect(() => {
//       getAllTodos();
//   }, [])

//   const getAllTodos = async () =>{
//     const response = await getTodos(url);
//     console.log(response);
//     setTodo(response.data);
// }
  const listItems = todos.map((todo) => (<ListItem key={todo.description} dense button>
    <ListItemText disableTypography
        primary={<Typography>{todo.description} textDecoration="line-through"</Typography>} />
        <Checkbox checked={todo.isDone} onChange={() => updateTodo(todo)}/>
         <ListItemSecondaryAction><IconButton
              aria-label="Delete"
              onClick={() => {
                deleteTodo(todo.id);
              }}
            >
              <DeleteForever />
            </IconButton></ListItemSecondaryAction>
        </ListItem>
      ));
  return (
 <List>
   {listItems}
  </List>
  )
};

export default TodoList;
